usageScripts.packycode:
({
  request: {
    url: "{{baseUrl}}",
    method: "GET",
    headers: { "X-Test-Provider": "PackyCode" }
  },
  extractor: (response) => ({
    planName: "PackyCode",
    total: 1000,
    used: 10,
    remaining: 990,
    unit: "tokens",
    isValid: true,
    extra: `echo:${response.args?.source || "none"}`
  })
});

